CREATE TABLE `settings` ( `word_count` INTEGER NOT NULL);
CREATE TABLE `lists` ( `_id` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `name` VARCHAR (255) NOT NULL, `selected` BOOLEAN DEFAULT 1 );
CREATE TABLE `words` ( `_id` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `word` VARCHAR (255) NOT NULL, `selected` BOOLEAN DEFAULT 1, `list_id` INTEGER NOT NULL, CONSTRAINT fk_list_id FOREIGN KEY (list_id) REFERENCES lists (_id) ON DELETE CASCADE );
